<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/** * Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';require_once 'includes/auth.php';require_once 'class/brand.php';$brandObject 	= new class_brand();/* Check posted data. */if(count($_POST) > 0) {	$errorArray	= array();	$data		= array();	$formValid	= true;	$success	= NULL;		if(isset($_POST['brand_code']) && trim($_POST['brand_code']) != '') {		/* Get the supplier. */		$brandData = $brandObject->getByCode(trim($_POST['brand_code']));				if(!$brandData) {			$errorArray['brand_code'] = 'Brand does not exist';			$formValid = false;		}	}	if(count($errorArray) == 0 && $formValid == true) {		if(isset($brandData)) {			$zfsession->brand 	= trim($_POST['brand_code']);		}		header('Location: /');		exit;	}	/* if we are here there are errors. */	$smarty->assign('errorArray', $errorArray);	}$brandPairs = $brandObject->pairs();if($brandPairs) $smarty->assign('brandPairs', $brandPairs);/* Display the template */	$smarty->display('default.tpl');?>