<?phperror_reporting(E_ALL);/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/* standard config include. */require_once 'config/database.php';require_once 'config/smarty.php';//include the Zend class for Authentificationrequire_once 'Zend/Session.php';// Set up the namespace$zfsession	= new Zend_Session_Namespace('ADMIN_SITE');$parse		= parse_ini_file($_SERVER['DOCUMENT_ROOT']."/config/settings.ini", true);if(isset($parse[$_SERVER['HTTP_HOST']])) {	$zfsession->config = $parse[$_SERVER['HTTP_HOST']];} else {	echo 'No settings file present...';	exit;}// Check if logged in, otherwise redirectif (!isset($zfsession->identity) || is_null($zfsession->identity) || $zfsession->identity == '') {	header('Location: /login.php');	exit();} else {	//instantiate the users class	require_once 'class/admin.php';	require_once 'class/brand.php';		$adminObject 	= new class_admin();	$brandObject 	= new class_brand();		//get user details by username	$adminData = $adminObject->getByCode($zfsession->identity);		$smarty->assign('adminData', $adminData);	/* admin selected supplier. */	$zfsession->adminData	= $adminData;	$zfsession->brand		= isset($zfsession->brand) && trim($zfsession->brand) != '' ? $zfsession->brand  : null;	if($zfsession->brand != null) {		$tempBrand = $brandObject->getByCode($zfsession->brand);		if($tempBrand) {			$zfsession->activeBrand = $tempBrand;			$smarty->assign('brand', $zfsession->brand);			$smarty->assign('activeBrand', $tempBrand);		}	}		unset($brandObject, $adminObject);	}$smarty->assign('config', $zfsession->config);global $zfsession;?>